import axios from "axios";

export const fetchImages = async () => {
    return await axios.get('https://boiling-refuge-66454.herokuapp.com/images').then(res => res.data)
}

export const getSingleImage = async (imageId) => {
    return await axios.get(`https://boiling-refuge-66454.herokuapp.com/images/${imageId}`).then(res => res.data)
}

export const postComment = async (imageId, options = {}) => {
    return await axios.post(`https://boiling-refuge-66454.herokuapp.com/images/${imageId}/comments`, options).then(res => res.data)
}